{
  "type": "object",
  "properties": {
    "tweets": {
      "type": "array",
      "description": "Array of 3-10 tweets",
      "items": {
        "type": "object",
        "properties": {
          "content": { "type": "string", "description": "Tweet text under 280 chars" },
          "type": { "type": "string", "description": "Type: contrarian, tactical, story, insight, question" }
        }
      }
    },
    "linkedin_hook": { "type": "string" },
    "linkedin_body": { "type": "string" },
    "linkedin_question": { "type": "string" },
    "key_ideas": {
      "type": "array",
      "items": { "type": "string" }
    }
  },
  "required": ["tweets", "linkedin_hook", "linkedin_body", "linkedin_question", "key_ideas"]
}

