// Generate a unique session ID for memory
const formData = $('Form Trigger').first().json;

return [{
  json: {
    ...formData,
    sessionId: `session-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`
  }
}];

// Format the AI Agent output for response
const agentOutput = $('AI Agent').first().json;

return [{
  json: {
    success: true,
    output: agentOutput.output || agentOutput.text || JSON.stringify(agentOutput),
    message: `‚úÖ Content Generated Successfully!\n\n${agentOutput.output || agentOutput.text || 'See output above'}`
  }
}];
