const formData = $('Form Trigger').first().json;
const tweetCount = parseInt(formData['Number of Tweets']) || 5;

return [{
  json: {
    ...formData,
    sessionId: `session-${Date.now()}`,
    tweetCount: Math.min(Math.max(tweetCount, 3), 10),
    postToX: formData['Post to X?'] === 'Yes - Post all tweets'
  }
}];

