// Convert tweets array to individual items with delay scheduling
const agentOutput = $('AI Agent').first().json;
const output = agentOutput.output || agentOutput;
const tweets = output.tweets || [];

if (!tweets || tweets.length === 0) {
  return [{
    json: {
      index: 0,
      total: 0,
      content: '',
      type: 'error',
      error: 'No tweets generated',
      output: output
    }
  }];
}

// Return all tweets as separate items
return tweets.map((tweet, index) => ({
  json: {
    index: index + 1,
    total: tweets.length,
    content: tweet.content || '',
    type: tweet.type || 'unknown',
    isFirst: index === 0,
    isLast: index === tweets.length - 1,
    output: output
  }
}));

// Collect results from all posted tweets
