parameters.jsCode:
const geminiOutput = $('Curate Content').first().json;
const response = geminiOutput.text || geminiOutput.content || '';
const input = $('Execute Workflow Trigger').first().json;

let curated;
try {
  const cleaned = response.replace(/```json\n?/gi, '').replace(/```\n?/gi, '').trim();
  const match = cleaned.match(/\{[\s\S]*\}/);
  if (match) {
    curated = JSON.parse(match[0]);
  } else {
    throw new Error('No JSON found');
  }
} catch (e) {
  curated = { error: e.message, raw: response.substring(0, 500) };
}

return [{
  json: {
    video_id: input.video_id,
    video_title: input.video_title,
    curated: curated,
    curated_at: new Date().toISOString()
  }
}];
