parameters.jsCode:
// Get Gemini HTTP response
const geminiOutput = $('Curate Content').first().json;

// Gemini REST API returns: candidates[0].content.parts[0].text
let response = '';
try {
  response = geminiOutput.candidates?.[0]?.content?.parts?.[0]?.text || '';
} catch (e) {
  response = '';
}

const input = $('Execute Workflow Trigger').first().json;

let curated;
try {
  const cleaned = (response || '').replace(/```json\n?/gi, '').replace(/```\n?/gi, '').trim();
  const match = cleaned.match(/\{[\s\S]*\}/);
  if (match) {
    curated = JSON.parse(match[0]);
  } else {
    throw new Error('No JSON found in response');
  }
} catch (e) {
  curated = { 
    error: e.message, 
    raw: (response || '').substring(0, 500),
    curated_tweets: [],
    curated_linkedin: [],
    curated_newsletter: {},
    summary: { glm_wins: 0, kimi_wins: 0, combined: 0 }
  };
}

return [{
  json: {
    video_id: input.video_id,
    video_title: input.video_title,
    curated: curated,
    curated_at: new Date().toISOString()
  }
}];
