// Parse GLM-4.6 response
const glmOutput = $('Extract Ideas (GLM-4.6)').first().json;
const glmContent = glmOutput.choices?.[0]?.message?.content || '';

// Parse Kimi-K2 response
const kimiOutput = $('Extract Ideas (Kimi-K2)').first().json;
const kimiContent = kimiOutput.choices?.[0]?.message?.content || '';

// Get input metadata
const input = $('Execute Workflow Trigger').first().json;

function parseJson(content, source) {
  try {
    const cleaned = content.replace(/```json\n?/gi, '').replace(/```\n?/gi, '').trim();
    const match = cleaned.match(/\{[\s\S]*\}/);
    if (match) {
      return { source, data: JSON.parse(match[0]), error: null };
    }
    throw new Error('No JSON found');
  } catch (e) {
    return { source, data: null, error: e.message };
  }
}

const glmParsed = parseJson(glmContent, 'GLM-4.6');
const kimiParsed = parseJson(kimiContent, 'Kimi-K2');

return [{
  json: {
    video_id: input.video_id,
    video_title: input.video_title,
    video_url: input.video_url,
    niche: input.niche,
    target_audience: input.target_audience,
    tone: input.tone,
    tweet_count: input.tweet_count,
    linkedin_count: input.linkedin_count,
    transcript: input.transcript,

    ideas_glm: glmParsed,
    ideas_kimi: kimiParsed,

    extracted_at: new Date().toISOString()
  }
}];
