// Get form input
const formData = $('Form Trigger').first().json;

// Get workflow list from API
const workflowsResponse = $('Get Workflow IDs').first().json;
const workflows = workflowsResponse.data || [];

// Helper function to find workflow ID by name
function getWorkflowId(name) {
  const workflow = workflows.find(w => w.name === name);
  return workflow ? workflow.id : null;
}

// Resolve workflow IDs
const workflowIds = {
  ideaExtraction: getWorkflowId('Idea Extraction (Parallel)'),
  contentGeneration: getWorkflowId('Content Generation (Parallel)'),
  curation: getWorkflowId('Content Curation (Gemini)')
};

// Check if all workflows found
const missingWorkflows = [];
if (!workflowIds.ideaExtraction) missingWorkflows.push('Idea Extraction (Parallel)');
if (!workflowIds.contentGeneration) missingWorkflows.push('Content Generation (Parallel)');
if (!workflowIds.curation) missingWorkflows.push('Content Curation (Gemini)');

if (missingWorkflows.length > 0) {
  throw new Error(`Missing workflows: ${missingWorkflows.join(', ')}. Please import them first.`);
}

return [{
  json: {
    // Original form data
    status: '‚è≥',
    video_id: `${Date.now()}-${Math.random().toString(36).substr(2, 5)}`,
    video_title: formData['Video Title'],
    video_url: formData['Video URL'] || '',
    transcript: formData['Transcript'],
    niche: formData['Content Niche'] || 'AI/Business',
    target_audience: formData['Target Audience'] || 'Entrepreneurs',
    tone: formData['Tone'] || 'practical',
    tweet_count: formData['Tweet Count'] || 5,
    linkedin_count: formData['LinkedIn Count'] || 3,
    created_at: new Date().toISOString(),

    // Resolved workflow IDs
    workflow_ids: workflowIds
  }
}];

const input = $('Prepare Data & Resolve IDs').first().json;
const ideaResult = $('Run Idea Extraction').first().json;
const contentResult = $('Run Content Generation').first().json;
const curationResult = $('Run Curation').first().json;

// Check for errors
const errors = [];
if (ideaResult?.error) errors.push(`Idea Extraction: ${ideaResult.error}`);
if (contentResult?.error) errors.push(`Content Generation: ${contentResult.error}`);
if (curationResult?.error) errors.push(`Curation: ${curationResult.error}`);

const hasErrors = errors.length > 0;
const status = hasErrors ? '‚ùå' : '‚úÖ';

// Count generated content
const tweets = curationResult?.curated?.curated_tweets?.length || 0;
const linkedin = curationResult?.curated?.curated_linkedin?.length || 0;
const newsletter = curationResult?.curated?.curated_newsletter ? 1 : 0;

return [{
  json: {
    status: status,
    video_id: input.video_id,
    video_title: input.video_title,
    success: !hasErrors,
    errors: errors,
    generated: {
      tweets: tweets,
      linkedin: linkedin,
      newsletter: newsletter
    },
    message: hasErrors 
      ? `‚ùå Processing completed with errors:\n${errors.join('\n')}`
      : `‚úÖ Content generated successfully!\n\nüìä Created:\n‚Ä¢ ${tweets} Tweets\n‚Ä¢ ${linkedin} LinkedIn posts\n‚Ä¢ ${newsletter} Newsletter section\n\nCheck your Google Sheet for the curated content.`
  }
}];
